/**
 * 使用动态编程方式计算特定位置的斐波那契数列的值
 *
 * @param n
 * @return {number}
 */
export default function fibonacciNth(n) {
    let currentValue = 1;
    let previousValue = 0;

    if (n === 1) {
      return 1;
    }

    let iterationsCounter = n - 1;

    while (iterationsCounter) {
      currentValue += previousValue;
      previousValue = currentValue - previousValue;

      iterationsCounter -= 1;
    }

    return currentValue;
}

